INSERT INTO book (idBook, titleBook, ISBN, summary, price, nbStock, personnalizedWord, trends, idCategory, idPublisher) VALUES (1, 'Un air de familles', '2360800701', 'Maman et moi, on vit toutes les deux dans la savane. Mes parents m''ont adopté quand j''étais tout petit. Papa et maman sont nés dans deux pays différents. Avec mes deux papas, on va souvent au musée. Nous, on n''a pas d''enfant. Les animaux de ce livre à jouer s''alignent par familles, toutes semblables et toutes différentes. Au lecteur de les distinguer grâce aux indices du texte. Des familles monoparentales aux « tribus », en passant par les familles homoparentales et recomposées, personne n''est laissé de côté ! Un album sensible et tendre pour s''amuser, mais aussi pour apprendre de nos différences.', 16, 8, 'J''ai beaucoup aimé', 1, 1, 1);
INSERT INTO book (idBook, titleBook, ISBN, summary, price, nbStock, personnalizedWord, trends, idCategory, idPublisher) VALUES (2, 'La disparition de chou', '2955789666', 'Lola a trois ans et vit avec ses parents. Il y a aussi Chou, son doudou koala. Ces deux-là forment un duo inséparable. Mais, un jour, c''est la catastrophe : Chou est introuvable... Il a disparu ! Lola et ses parents le cherchent partout et mettent des avis de recherche dans toute la ville. Aucune réponse et le temps passe...', 14, 2, 'Livre moyen', 0, 1, 2);
INSERT INTO book (idBook, titleBook, ISBN, summary, price, nbStock, personnalizedWord, trends, idCategory, idPublisher) VALUES (3, 'Un arbre merveilleux', '2278089706', '«  Dans mon jardin se trouve un arbre merveilleux… ». Cet arbre majestueux révèle bien des secrets. Au gré du temps qui passe, il accueille le chat qui vient s''y abriter, les oiseaux qui viennent y nicher, un couple de loirs... Quand revient le printemps, dans son feuillage renaissant, on entend bientôt le murmure de nouveaux occupants... Et si tout recommençait ?', 13.5, 0, 'J''ai beaucoup aimé', 1, 1, 3);
INSERT INTO book (idBook, titleBook, ISBN, summary, price, nbStock, personnalizedWord, trends, idCategory, idPublisher) VALUES (4, 'Imagier mouvementé', '1035200783', 'Et hop ! Le lièvre bondit, le chat s''étire, le panda roule... Des actions à regarder mais aussi à mimer ! Alors 1, 2, 3, prêts... partez !', 11.9, 10, null, 0, 1, 12);
